def return_list_of_dots(d_dots,current_plane,x_offset=0,y_offset=0,dot_color=WHITE,dot_radius = 0.15):
    # Given a dictionary of vertex names and locations
    # return a list of Dot objects for placing them on a grid
    lis = []
    for location in d_dots:
        lis.append(Dot(point=current_plane.c2p(location[0]+x_offset,location[1]+y_offset),radius=dot_radius).set_fill(dot_color))
    return lis

def construct_lines_from_dots(dots,s_width=15,line_color=WHITE):
    lines = []
    for k in range(len(dots)-1):
        from_dot = dots[k]
        to_dot = dots[k+1]
        lines.append(Line(from_dot.get_center(),to_dot.get_center(),stroke_width=s_width).set_color(line_color))
    return lines

def get_range_of_dots(dots):
    DataRange = namedtuple("Range","x y")
    xlist = []
    ylist = []
    for item in dots:
        xlist.append(item[0])
        ylist.append(item[1])
    xrange = max(xlist) - min(xlist)
    yrange = max(ylist) - min(ylist)
    return DataRange(xrange,yrange)


